import React, { useState } from 'react';
import './App.css';
import MindMap from './components/MindMap';
import Navbar from './components/Navbar';
import ResourceSidebar from './components/ResourceSidebar';
import SearchBar from './components/SearchBar';
import FeaturedResources from './components/FeaturedResources';

function App() {
  const [searchQuery, setSearchQuery] = useState('');
  const [activeView, setActiveView] = useState<'mindmap' | 'resources'>('mindmap');

  const handleSearch = (query: string) => {
    setSearchQuery(query);
    console.log('Searching for:', query);
  };

  return (
    <div className="App">
      <Navbar />
      <div className="background">
        <div className="view-toggle">
          <button 
            className={`toggle-btn ${activeView === 'mindmap' ? 'active' : ''}`}
            onClick={() => setActiveView('mindmap')}
          >
            Career Mind Map
          </button>
          <button 
            className={`toggle-btn ${activeView === 'resources' ? 'active' : ''}`}
            onClick={() => setActiveView('resources')}
          >
            Featured Resources
          </button>
        </div>
        <div className="card">
          <div className="app-container">
            <ResourceSidebar />
            <div className="main-content">
              <SearchBar onSearch={handleSearch} />
              {activeView === 'mindmap' ? <MindMap /> : <FeaturedResources />}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
