import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';


// Configure the Supabase client (replace these with your actual values)
const supabaseUrl = "https://buwbxnzdbgszvfqxmgfy.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImJ1d2J4bnpkYmdzenZmcXhtZ2Z5Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NDI2Mjc4ODQsImV4cCI6MjA1ODIwMzg4NH0.ZLLi-76N_X2t-2NUpbVj1VWHhVT6nMkPZCoAYdbKa50";
const supabase = createClient(supabaseUrl, supabaseKey);

const UniversitySelector: React.FC = () => {
  // Predefined universities for the dropdown
  const predefinedUniversities = [
    "New Jersey Institute of Technology",
    "Rutgers New Brunswick"
  ];

  const [selectedUniversity, setSelectedUniversity] = useState(predefinedUniversities[0]);
  const [newUniversity, setNewUniversity] = useState('');
  const [message, setMessage] = useState('');

  const handleAddUniversity = async () => {
    if (newUniversity.trim() === '') {
      setMessage("Please enter a university name.");
      return;
    }
    // Insert the new university into the 'university_courses' table with a dummy JSON object for matched_courses.
    const { data, error } = await supabase
      .from('university_courses')
      .insert([{ university_name: newUniversity, matched_courses: {} }]);

    if (error) {
      console.error("Error adding university:", error);
      setMessage("Error adding university.");
    } else {
      console.log("Added university:", data);
      setMessage("University added successfully!");
      // Optionally clear the input field
      setNewUniversity('');
    }
  };

  return (

    <div className="justify-center">
    <div style={{ margin: '20px 0', display: 'flex', alignItems: 'center' }}>
      <label htmlFor="university-dropdown">Select a university: </label>
      <select 
        id="university-dropdown" 
        value={selectedUniversity}
        onChange={(e) => setSelectedUniversity(e.target.value)}
        style={{ marginLeft: '10px' }}
      >
        {predefinedUniversities.map((uni, index) => (
          <option key={index} value={uni}>{uni}</option>
        ))}
      </select>
      <span style={{ marginLeft: '20px' }}>or add your university:</span>
      <input 
        type="text" 
        placeholder="Enter university name" 
        value={newUniversity} 
        onChange={(e) => setNewUniversity(e.target.value)}
        style={{ marginLeft: '10px' }}
      />
      <button onClick={handleAddUniversity} style={{ marginLeft: '10px' }}>
        Add University
      </button>
      {message && <p style={{ marginLeft: '20px' }}>{message}</p>}
    </div>
    </div>
  );
};

export default UniversitySelector;
