import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';


// Configure the Supabase client (replace these with your actual values)
const supabaseUrl = "https://buwbxnzdbgszvfqxmgfy.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImJ1d2J4bnpkYmdzenZmcXhtZ2Z5Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NDI2Mjc4ODQsImV4cCI6MjA1ODIwMzg4NH0.ZLLi-76N_X2t-2NUpbVj1VWHhVT6nMkPZCoAYdbKa50";
const supabase = createClient(supabaseUrl, supabaseKey);

const UniversitySelector: React.FC = () => {
  // Predefined universities for the dropdown
  const predefinedUniversities = [
    "New Jersey Institute of Technology",
    "Rutgers New Brunswick"
  ];

  const [selectedUniversity, setSelectedUniversity] = useState(predefinedUniversities[0]);
  const [newUniversity, setNewUniversity] = useState('');
  const [message, setMessage] = useState('');

  const handleAddUniversity = async () => {
    if (newUniversity.trim() === '') {
      setMessage("Please enter a university name.");
      return;
    }
    // Insert the new university into the 'university_courses' table with a dummy JSON object for matched_courses.
    const { data, error } = await supabase
      .from('university_courses')
      .insert([{ university_name: newUniversity, matched_courses: {} }]);

    if (error) {
      console.error("Error adding university:", error);
      setMessage("Error adding university.");
    } else {
      console.log("Added university:", data);
      setMessage("University added successfully!");
      // Optionally clear the input field
      setNewUniversity('');
    }
  };

  return (
        <div className="max-w-2xl mx-auto mt-6 p-6 bg-white rounded-lg shadow-md">
          {/* Heading */}
          <h2 className="text-2xl font-bold text-gray-800 mb-2 text-center">
            Select or Add Your University
          </h2>
          <p className="text-center text-gray-500 mb-6">
            Weâ€™ll use this to match relevant courses for your career path.
          </p>
    
          {/* Select a university */}
          <div className="flex flex-col sm:flex-row items-center justify-center space-y-2 sm:space-y-0 sm:space-x-4 mb-4">
            <label 
              htmlFor="university-dropdown" 
              className="text-gray-700 font-medium"
            >
              Select a university:
            </label>
            <select
              id="university-dropdown"
              value={selectedUniversity}
              onChange={(e) => setSelectedUniversity(e.target.value)}
              className="border border-gray-300 rounded px-3 py-2 text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-300"
            >
              {predefinedUniversities.map((uni, index) => (
                <option key={index} value={uni}>
                  {uni}
                </option>
              ))}
            </select>
          </div>
    
          {/* Divider */}
          <div className="relative my-4">
            <div className="absolute inset-0 flex items-center">
              <div className="w-full border-b border-gray-300"></div>
            </div>
            <div className="relative flex justify-center">
              <span className="bg-white px-2 text-gray-500">or add your university</span>
            </div>
          </div>
    
          {/* Add a new university */}
          <div className="flex flex-col sm:flex-row items-center justify-center space-y-2 sm:space-y-0 sm:space-x-4">
            <input
              type="text"
              placeholder="Enter university name"
              value={newUniversity}
              onChange={(e) => setNewUniversity(e.target.value)}
              className="border border-gray-300 rounded px-3 py-2 w-64 text-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-300"
            />
            <button
              onClick={handleAddUniversity}
              className="bg-blue-600 text-white font-semibold rounded px-4 py-2 hover:bg-blue-700 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-300"
            >
              Add University
            </button>
          </div>
    
          {/* Status Message */}
          {message && (
            <p className="text-center text-green-600 mt-4">
              {message}
            </p>
          )}
        </div>

  );
};

export default UniversitySelector;
