import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';

// Move these to environment variables (.env)
const supabaseUrl = "https://buwbxnzdbgszvfqxmgfy.supabase.co";
const supabaseKey = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImJ1d2J4bnpkYmdzenZmcXhtZ2Z5Iiwicm9sZSI6ImFub24iLCJpYXQiOjE3NDI2Mjc4ODQsImV4cCI6MjA1ODIwMzg4NH0.ZLLi-76N_X2t-2NUpbVj1VWHhVT6nMkPZCoAYdbKa50";
const supabase = createClient(supabaseUrl!, supabaseKey!);

const UniversitySelector: React.FC = () => {
  // Predefined universities for the dropdown
  const predefinedUniversities = [
    "New Jersey Institute of Technology",
    "Rutgers New Brunswick",
    "University of Texas, Arlington",
    "North Carolina A&T University",
    "Kennesaw State University",
    "Howard University",
    "Tennessee State University",
    "Johnson C. Smith University",
    "University of North Carolina, Charlotte"

  ];
  
  

  const [selectedUniversity, setSelectedUniversity] = useState(predefinedUniversities[0]);
  const [newUniversity, setNewUniversity] = useState('');
  const [message, setMessage] = useState('');

  const handleAddUniversity = async () => {
    if (newUniversity.trim() === '') {
      setMessage("Please enter a university name.");
      return;
    }
    // Insert the new university into the 'university_courses' table with a dummy JSON object for matched_courses.
    const { data, error } = await supabase
      .from('university_courses')
      .insert([{ university_name: newUniversity, matched_courses: {} }]);

    if (error) {
      console.error("Error adding university:", error);
      setMessage("Error adding university.");
    } else {
      console.log("Added university:", data);
      setMessage("University added successfully!");
      // Optionally clear the input field
      setNewUniversity('');
    }
  };

  const initializeUniversities = async () => {
    const universities = [
      "New Jersey Institute of Technology",
      "Rutgers New Brunswick",
      "University of Texas, Arlington",
      "North Carolina A&T University",
      "Kennesaw State University",
      "Howard University",
      "Tennessee State University",
      "Johnson C. Smith University",
      "University of North Carolina, Charlotte"
    ];

    for (const university of universities) {
      // First check if university exists
      const { data: existingUniversity } = await supabase
        .from('university_courses')
        .select('university_name')
        .eq('university_name', university)
        .single();

      // Only insert if university doesn't exist
      if (!existingUniversity) {
        const { data, error } = await supabase
          .from('university_courses')
          .insert([{ 
            university_name: university, 
            matched_courses: {} 
          }])
          .select();

        if (error) {
          console.error(`Error adding ${university}:`, error);
        } else {
          console.log(`Added ${university} successfully`);
        }
      } else {
        console.log(`${university} already exists in database`);
      }
    }
  };

  
  return (
    //fix the style of the dropdown later
    <div className="flex justify-center items-center p-5">
      <div className="flex flex-col md:flex-row items-center gap-4">
        <label 
          htmlFor="university-dropdown" 
          className="text-2xl font-extrabold text-indigo-600 uppercase tracking-wider"
        >
          Select a university:
        </label>
        
        <select
          id="university-dropdown"
          value={selectedUniversity}
          onChange={(e) => setSelectedUniversity(e.target.value)}
          className="rounded-lg border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 px-4 py-2"
        >
          {predefinedUniversities.map((uni, index) => (
            <option key={index} value={uni}>{uni}</option>
          ))}
        </select>

        <span className="text-gray-600 font-medium">or add your university:</span>

        <input
          type="text"
          placeholder="Enter university name"
          value={newUniversity}
          onChange={(e) => setNewUniversity(e.target.value)}
          className="rounded-lg border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 px-4 py-2"
        />

        <button 
          onClick={handleAddUniversity}
          className="bg-indigo-600 text-white px-6 py-2 rounded-lg hover:bg-indigo-700 transition-colors"
        >
          Add University
        </button>

        {message && (
          <p className={`text-sm ${message.includes('Error') ? 'text-red-600' : 'text-green-600'}`}>
            {message}
          </p>
        )}
      </div>
    </div>
  );
};

export default UniversitySelector;
